import React, { useEffect, useState } from 'react'

function TABox ( {locationId}) {
    const [item, setItem] = useState(null)
    const [itemImages, setItemImages] = useState(null)

    const { name, brand,  } = item;

    console.log(item)

    useEffect( () => {
        const options = {method: 'GET', headers: {Accept: 'application/json'}};

        fetch(`https://api.content.tripadvisor.com/api/v1/location/${locationId}/details?language=en&currency=USD&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
        .then(response => response.json())
        .then(response => setItem(response))
        .catch(err => console.error(err));
    }, [locationId])

    useEffect( () => {
        const options = {method: 'GET', headers: {Accept: 'application/json'}};

        fetch(`https://api.content.tripadvisor.com/api/v1/location/${locationId}/photos?language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
        .then(response => response.json())
        .then(response => setItemImages(response))
        .catch(err => console.error(err));
    }, [locationId])

    return (

        <div className="TABox">
            {item !== null && itemImages !== null ? <>
            <div className="TA-images">
                <div>{itemImages.data ? <img src={itemImages.data[1].images.large.url}/> : null}</div>
                {/* <div>{itemImages.data ? <img src={itemImages.data[0].images.medium.url}></img> : null}</div> */}
            </div>
            <div className="TA-content">
                <h3>{item.brand || item.name ? item.name + " (by " + item.brand + ")": null}</h3>
                <h5>{item.address_obj !== false ? item.address_obj.city + ", " + item.address_obj.country : null}</h5>
                <p>{item.category !== false ? "Category: " + item.category.name : null}</p>
                <div>{item.subcategory !== false ? item.subcategory.name : null}</div>
                <div className="rating">
                    <div>{"Rating: "}</div>
                    <div>{item.rating_image_url ? <img src={item.rating_image_url} alt={item.name}/>: null}</div>
                    <div>{item.rating ? "(" + item.rating + ")": null}</div>
                    <div>{item.price_level ? "Price: " + item.price_level : null}</div>
                </div>
                <p>{item.description ? item.description.slice(0,160) + "..." : null}</p>
            </div>
            <div className="TA-review">
                <div className="number">{item.ranking_data ? item.ranking_data.ranking_string.slice(0,3) : null}</div>
                <div>{item.ranking_data ? item.ranking_data.ranking_string.slice(3) : null}</div>     
            </div>
            </> 
            : null}
        </div>  

    );
}

export default TABox;
