import React, {useState, useEffect} from 'react'
import './App.css';
import Header from './Header.js'
import { Route, Routes, useNavigate} from "react-router-dom";
import LeftMenu from './LeftMenu';
import TopMid from './TopMid';
import Footer from './Footer';
import Home from './Home';
import Country from './Country';
import Activity from './Activity';
import SearchResults from './SearchResults';
import AdSense from 'react-adsense';

function App() {

  const [url, setUrl] = useState("")
  const [search, setSearch] = useState("")

  let navigate = useNavigate();

  console.log("outside top " + window.location.href)

  useEffect( () => {

    const options = {method: 'GET', headers: {Accept: 'application/json'}};

    fetch('https://api.content.tripadvisor.com/api/v1/location/12877674/reviews?language=en&key=D8942E614BA64F8D8A5DBA66B6D80449', options)
      .then(response => response.json())
      .then(response => console.log(response))
      .catch(err => console.error(err));
  }, [])

  useEffect(() => {
        setUrl(window.location.href)
        console.count('rerender counter')
  }, [url])

  function onSearch (search) {
    setSearch(search)
    setUrl(window.location.protocol +"//" + window.location.hostname + ":" + window.location.port + `/search/${search}`)
    console.log(window.location.protocol +"//" + window.location.hostname +  ":" + window.location.port + `/search/${search}`)
    navigate(`search/${search}`)
  }

  function onCountryClick(newURL){
    setUrl(newURL)
    console.log("in country click " + window.location.href + newURL)
  }
  function onFooterClick (newUrl){
    setUrl(newUrl)
    console.log("in footer click " + window.location.href)
  }

  return (
    <div className="App">
      <Header onSearch={onSearch} />
      <div className="home">
        <LeftMenu onCountryClick={onCountryClick} />
        <TopMid url={url}/>
      </div> 
      <Footer url={url} onFooterClick={onFooterClick}/>
      <Routes>
                <Route exact path="/" element={<Home url={url}/>}></Route>
                <Route path="/country">
                    <Route path=":country" element={<Country />}>
                        <Route path=":city" element={<Country />}/>
                    </Route>
                </Route>
                <Route path="search/:results" element={<SearchResults />}></Route>
                <Route path="/:activities" element={<Activity />}></Route>
      </Routes>
    </div>
  );
}

export default App;
