      case 'hotels':
        return 'best hotels in Europe'
      case 'flights':
        return 'Europe flights'
      case 'activities':
         return 'Europe activities'
      case 'restaurants':
        return 'best restaurants in europe'
      case result :
        return result
      // case result && window.location.href.includes('country'):
      //   return 'travel to' + result  
      default:
        return 'travel europe'
    }
  }

    let pageOptions = {
        "pubId": "partner-pub-1187797442630842", // Make sure this is the correct client ID!
        "query": `${resultsCase(results)}`, // Make sure the correct query is placed here!
