import React from 'react'
import { useParams, useLocation, useMatch } from 'react-router-dom';

function TextAd ( {results, city}) {

  let urlArray = window.location.href.split('/')

  let clickElement = () => this.tagName;
  let clickContent = () => this.innerHTML;
  console.log(document.getElementsByTagName('H4')[1].innerHTML)
  console.log(clickContent)

  function resultsCase (result, city){
    switch (result) {
      case result:
        if (window.location.href.includes('search')){
          return result
        }
        else if (urlArray.length === 6){
          return 'travel to ' + city
        }
        else if (window.location.href.includes('country')){
          return 'travel to ' + result
        } 
        else if (document.getElementsByTagName('H4')[0].innerHTML === 'Best Hotels in Paris' || document.getElementsByTagName('P')[0] === 'Explore'){
          return 'hotels in paris'
        }
        else if (document.getElementsByTagName('H4')[1].innerHTML === 'Flights to Barcelona' || document.getElementsByTagName('P')[1] === clickElement){
          return 'flights to barcelona'
        }
        else if (document.getElementsByTagName('H4')[2].innerHTML === 'Top Restaurants in Venice'|| document.getElementsByTagName('P')[2] === clickElement){
          return 'hotels in paris'
        }
        break
      case 'hotels':
        return 'best hotels in Europe'
      case 'flights':
        return 'Europe flights'
      case 'activities':
         return 'Europe activities'
      case 'restaurants':
        return 'best restaurants in europe'

      default:
        return 'travel europe'
    }
  }


    let pageOptions = {
        "pubId": "partner-pub-1187797442630842", // Make sure this is the correct client ID!
        "query": `${resultsCase(results, city)}`, // Make sure the correct query is placed here!
        "styleId": "5769253257",
        "adsafe": "high"
      };

    let adblock1 = {
        "container": "afscontainer1"
    }; 
    window._googCsa('ads', pageOptions, adblock1);

  return (
    <>
      <div id="afscontainer1"></div>

    </>
  );
}

export default TextAd;
