import React, {useState, useEffect} from 'react'
import './App.css';
import Header from './Header.js'
import { Route, Routes, useNavigate} from "react-router-dom";
import LeftMenu from './LeftMenu';
import TopMid from './TopMid';
import Footer from './Footer';
import Home from './Home';
import Country from './Country';
import Activity from './Activity';
import SearchResults from './SearchResults';
import Filters from './Filters';
import {latLong} from './global.js'
import AdSense from 'react-adsense';

function App() {

  const [url, setUrl] = useState("http://localhost:3000/")
  const [search, setSearch] = useState("")
  const [searchValue, setSearchValue] = useState(null)
  const [filterValue, setFilterValue] = useState(null)

  let navigate = useNavigate();

  console.log("outside top " + window.location.href)

  useEffect(() => {
        setUrl(window.location.href)
        if (url.includes("search")){
          onSearch(url.split('/').slice(4))
        }
        else if (url.includes("search") && url.split('/').slice(4).includes('-')){
          onFilterSubmit()
        }
        // else if ()
        console.count('rerender counter')
  }, [url])

  function onSearch (search) {
    setSearch(search)
    const options = {method: 'GET', headers: {Accept: 'application/json'}};

    fetch(`https://api.content.tripadvisor.com/api/v1/location/search?searchQuery=${search}&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setSearchValue(response.data))
      .catch(err => console.error(err));

    setUrl(window.location.href)
    // setUrl(window.location.protocol +"//" + window.location.hostname + ":" + window.location.port + `/search/${search}`)
    // console.log(window.location.protocol +"//" + window.location.hostname +  ":" + window.location.port + `/search/${search}`)
    navigate(`search/${search}`)
  }

  function onCountryClick(newURL){
    setUrl(window.location.href)
    console.log("in country click " + window.location.href + newURL)
  }
  function onFooterClick (newUrl){
    setUrl(window.location.href)

    console.log("in footer click " + window.location.href + newUrl)
  }
  function onFilterSubmit(country, city, category){ 
    setSearchValue(null)
    let coordinates = latLong.filter( item => item.city === city)
    console.log(coordinates)
    const options = {method: 'GET', headers: {Accept: 'application/json'}};

    fetch(`https://api.content.tripadvisor.com/api/v1/location/nearby_search?latLong=${coordinates[0].lat},${coordinates[0].long}&category=${category}&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setFilterValue(response.data))
      .catch(err => console.error(err));

    navigate(`search/${country}-${city}-${category}`)
  }

  return (
    <div className="App">
      <Header onSearch={onSearch} />
      <Filters onFilterSubmit={onFilterSubmit}/>
      <Routes>
        <Route exact path="/" element={<Home onCountryClick={onCountryClick} url={url} searchValue={searchValue}/>}></Route>
        <Route path="/country">
            <Route path=":country" element={<Country url={url}/>}>
                <Route path=":city" element={<Country url={url}/>}/>
            </Route>
        </Route>
        <Route path="search/:results" element={<SearchResults searchValue={searchValue} filterValue={filterValue}/>}></Route>
        <Route path="/:activities" element={<Activity url={url} />}></Route>
      </Routes>
      <Footer url={url} onFooterClick={onFooterClick}/>
    </div>
  );
}

export default App;
