import React, {useState, useEffect} from 'react'
import { useParams } from 'react-router-dom';
import TextAd from "./TextAd.js"
import TABox from './TABox.js';
import {latLong} from './global.js'


function Country({url}) {

  let { city, country } = useParams();

  const [locationId, setLocationId] = useState(null)
  useEffect( () => {

    let cityParam = latLong.filter( cityItem => cityItem.city === city)
    // let countryParam = 
    const options = {method: 'GET', headers: {Accept: 'application/json'}};

    if (url.split("/").length > 5){
      fetch(`https://api.content.tripadvisor.com/api/v1/location/nearby_search?latLong=${cityParam[0].lat},${cityParam[0].long}&category=hotels&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setLocationId(response.data))
      .catch(err => console.error(err));
    }
    else {
      fetch(`https://api.content.tripadvisor.com/api/v1/location/search?searchQuery=${country}&category=restaurants&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setLocationId(response.data))
      .catch(err => console.error(err));
    }
  }, [url, city, country])


  return (
    <>

    {locationId === null ? null : locationId.map( item => <TABox locationId={item.location_id}/>) } 
      {/* This is a {country} {city} page */}

    </>
  );
}

export default Country;
