import React, { useEffect, useState } from 'react'

function TABox ( {locationId}) {
    const [item, setItem] = useState(null)
    const [itemImages, setItemImages] = useState(null)

    console.log(item)

    useEffect( () => {
        const options = {method: 'GET', headers: {Accept: 'application/json'}};

        fetch(`https://api.content.tripadvisor.com/api/v1/location/${locationId}/details?language=en&currency=USD&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
        .then(response => response.json())
        .then(response => setItem(response))
        .catch(err => console.error(err));
    }, [locationId])

    useEffect( () => {
        const options = {method: 'GET', headers: {Accept: 'application/json'}};

        fetch(`https://api.content.tripadvisor.com/api/v1/location/${locationId}/photos?language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
        .then(response => response.json())
        .then(response => setItemImages(response))
        .catch(err => console.error(err));
    }, [locationId])


    return (

        <div className="TABox">
            <div>{itemImages.data.images.medium.url !== null ? <img src={itemImages.data[0].images.medium.url}></img> : null}</div>
            <div>{item.name !== null ? item.name : null}</div>
            {/* <div>{itemImages !== null ? <img src={itemImages.data[1].images.medium.url}></img> : null}</div> */}
            <div>{item.description !== null ? item.description : null}</div>
            <div>{item.rating !== null ? item.rating : null}</div>
            <div>{item.ranking_data.ranking_string !== null ? item.ranking_data.ranking_string : null}</div>
            <div>{item.price_level !== null ? item.price_level : null}</div>
        </div>  

    );
}

export default TABox;
