import React, { useEffect, useState } from 'react'

function TABox ( {locationId}) {
    const [item, setItem] = useState(null)
    const [itemImages, setItemImages] = useState(null)



    useEffect( () => {
        const options = {method: 'GET', headers: {Accept: 'application/json'}};

        fetch(`https://api.content.tripadvisor.com/api/v1/location/${locationId}/details?language=en&currency=USD&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
        .then(response => response.json())
        .then(response => setItem(response))
        .catch(err => console.error(err));
    }, [locationId])

    useEffect( () => {
        const options = {method: 'GET', headers: {Accept: 'application/json'}};

        fetch(`https://api.content.tripadvisor.com/api/v1/location/${locationId}/photos?language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
        .then(response => response.json())
        .then(response => setItemImages(response))
        .catch(err => console.error(err));
    }, [locationId])

    return (

        <div className="TABox">
            {item !== null && itemImages !== null ? <>
            <div className="TA-images">
                <div>{itemImages.data ? <img src={itemImages.data[1].images.medium.url}></img> : null}</div>
                {/* <div>{itemImages.data ? <img src={itemImages.data[0].images.medium.url}></img> : null}</div> */}
            </div>
            <div className="TA-content">
                <div>{item.name !== false ? item.name : null}</div>
                <div>{item.address_obj !== false ? item.address_obj.city + ", " + item.address_obj.country : null}</div>
                <div>{item.category !== false ? item.category.name : null}</div>
                <div>{item.rating_image_url !== false ? item.rating_image_url + "(" + item.rating + ")" : null}</div>
                <div>{item.price_level !== false ? item.price_level : null}</div>
                <div>{item.description !== false ? item.description : null}</div>
            </div>
            <div className="TA-review">
                <div>{item.ranking_data ? item.ranking_data.ranking_string : null}</div>     
            </div>
            </> 
            : null}
        </div>  

    );
}

export default TABox;
