import React, {useEffect, useState} from 'react'
import { useParams } from 'react-router-dom';
import TextAd from "./TextAd.js"
import TABox from './TABox.js';


function Activity({url}) {

  let { activities } = useParams();

  const [locationId, setLocationId] = useState(null)

  useEffect( () => {

    const options = {method: 'GET', headers: {Accept: 'application/json'}};

    if (url.split("/").length > 5){
      fetch(`https://api.content.tripadvisor.com/api/v1/location/search?searchQuery=${city}&category=restaurants&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setLocationId(response.data))
      .catch(err => console.error(err));
    }
    else {
      fetch(`https://api.content.tripadvisor.com/api/v1/location/search?searchQuery=${country}&category=restaurants&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setLocationId(response.data))
      .catch(err => console.error(err));
    }
  }, [url, city, country])

  return (
    <>
        {/* This is a {activities} page */}
        {locationId === null ? null : locationId.map( item => <TABox locationId={item.location_id}/>) }
    </>
  );
}

export default Activity;
