import React, {useEffect, useState} from 'react'
import { useParams } from 'react-router-dom';
import TextAd from "./TextAd.js"
import TABox from './TABox.js';
import {latLong} from './global.js'


function Activity({url}) {

  let { activities } = useParams();
  const [locationId, setLocationId] = useState(null)

  useEffect( () => {

    const options = {method: 'GET', headers: {Accept: 'application/json'}};

    if (activities === "hotels-paris"){
      const options = {method: 'GET', url: `http://localhost:8000/nearby-back`, headers: {Accept: 'application/json'}};

      axios.request(options).then((response) => {
        setLocationId(response.data)
      })  
      .catch(err => console.error(err));
    }
    else if (activities === "hotels-barcelona"){
      fetch(`https://api.content.tripadvisor.com/api/v1/location/nearby_search?latLong=41.38%2C2.16&category=hotels&radius=6&radiusUnit=mi&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setLocationId(response.data))
      .catch(err => console.error(err));
    }
    else if (activities === "restaurants-rome"){
      fetch(`https://api.content.tripadvisor.com/api/v1/location/nearby_search?latLong=41.9%2C12.5&category=restaurants&radius=7&radiusUnit=mi&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
      .then(response => response.json())
      .then(response => setLocationId(response.data))
      .catch(err => console.error(err));
    }
  }, [url, activities])

  return (
    <>
        {locationId === null ? null : locationId.data.map( item => <TABox locationId={item.location_id}/>) }
    </>
  );
}

export default Activity;
