import React, {useState, useEffect} from 'react'
import { useParams } from 'react-router-dom';
import TextAd from "./TextAd.js"
import TABox from './TABox.js';



function Country() {

  let { city, country } = useParams();
  let urlArray = window.location.href.split('/')

  const [locationId, setLocationId] = useState(null)
  useEffect( () => {
    const options = {method: 'GET', headers: {Accept: 'application/json'}};

    fetch(`https://api.content.tripadvisor.com/api/v1/location/search?searchQuery=${urlArray[6]}&category=restaurants&language=en&key=D8942E614BA64F8D8A5DBA66B6D80449`, options)
    .then(response => response.json())
    .then(response => setLocationId(response))
    .catch(err => console.error(err));
  }, [urlArray])


  return (
    <>

      <TABox 
        locationId={locationId}
        /> 
      {/* This is a {country} {city} page */}

    </>
  );
}

export default Country;
