  const [dropdownCity, setDropdownCity] = useState("")

  function handleCountry(e){
    setDropdownCountry(e.target.value)
  }
  function handleCity(e){
    setDropdownCity(e.target.value)
  }
  const dropdownSelected = <option hidden selected>{`Select a City...`}</option>; {latLong.filter( country => countr)}
  const dropdownUnselected =  <option hidden selected>Please Select a Country First</option>
  return (
    <div className="filter-container">
        <label for="country">Country</label>
        <select value={dropdownCountry} onChange={handleCountry} name="country" id="country">
            <option hidden selected>Select a Country...</option>
            {countries.map( country => <option value={country}>{country}</option>)}
        </select>
        <br></br>
        <label for="city">City</label>
        <select value={dropdownCity} onChange={handleCity} name="country" id="country">
            {dropdownCountry ? dropdownSelected : dropdownUnselected} 
        </select>
    </div>
  );
}

export default Filters;
