import React, {useState, useEffect} from 'react'
import {countries, latLong} from './global.js'

function Filters({url}) {

  const [dropdownCountry, setDropdownCountry] = useState("")
  const [dropdownCity, setDropdownCity] = useState("")
  const [dropdownCategory, setDropdownCategory] = useState("")

  function handleCountry(e){
    setDropdownCountry(e.target.value)
  }
  function handleCity(e){
    setDropdownCity(e.target.value)
  }
  function handleCategory(e){
    setDropdownCategory(e.target.value)
  }
  function getCities (){
    let cities = latLong.filter( country => country.country === dropdownCountry)
    return cities;
  }

  const dropdownSelected = (<><option hidden selected>{`Select a City...`}</option> {getCities().map( city => <option value={city.city}>{city.city}</option>).slice(0,20)}</>)
  const dropdownUnselected =  <option hidden selected>Please Select a Country First</option>

  return (

    <div className="filter-container">
        <label for="country">Country</label>
        <select value={dropdownCountry} onChange={handleCountry} name="country" id="country">
            <option hidden selected>Select a Country...</option>
            {countries.map( country => <option value={country}>{country}</option>)}
        </select>
        <br></br>
        <label for="city">City</label>
        <select value={dropdownCity} onChange={handleCity} name="country" id="country">
            {dropdownCountry ? dropdownSelected : dropdownUnselected} 
        </select>
        <br></br>
        <label for="category">Category</label>
        <select value={dropdownCategory} onChange={handleCategory} name="category" id="category">
            <option hidden selected>Select a Category...</option>
            <option value="Hotels">Hotels</option>
            <option value="Restaurants">Restaurants</option>
            <option value="Attractions">Attractions</option>
            <option value="Geography">Geography</option>
        </select>
    </div>
  );
}

export default Filters;
